parameters.jsCode:
// Process the domain data with enhanced schema structure
const webhookData = $('Webhook Trigger').item.json.body;
const embeddingData = $('Generate Embedding').item.json;
const existingData = $('Find Existing Domain').item.json;

const domain = webhookData.domain;
const schema = webhookData.schema;
const action = webhookData.action || 'create';

// Extract embedding - use the same structure as debug workflow
const embedding = embeddingData.embedding;

// Find existing point ID for updates
let existingPointId = null;
if (existingData.result && existingData.result.points && existingData.result.points.length > 0) {
  existingPointId = existingData.result.points[0].id;
}

// Enhanced schema processing
const enhancedSchema = schema.map(field => {
  const enhancedField = {
    name: field.name,
    type: field.type
  };

  if (field.description) {
    enhancedField.description = field.description;
  }

  if (field.examples && Array.isArray(field.examples)) {
    enhancedField.examples = field.examples;
  }

  return enhancedField;
});

return [{
  json: {
    domain: domain,
    schema: enhancedSchema,
    embedding: embedding,
    existingPointId: existingPointId,
    hasExisting: existingPointId !== null,
    action: existingPointId ? 'update' : 'create',
    fieldCount: enhancedSchema.length,
    timestamp: new Date().toISOString()
  }
}];

