parameters.jsCode:
// Check if domain exists and get the first match
const existingData = $input.item.json;
const domain = $('Webhook Trigger').item.json.body.domain;
const newSchema = $('Webhook Trigger').item.json.body.schema;
const embedding = $('Generate Embedding').item.json.embedding;

let existingPointId = null;
if (existingData.result && existingData.result.points && existingData.result.points.length > 0) {
  // Use the first matching point ID
  existingPointId = existingData.result.points[0].id;
}

return [{
  json: {
    domain: domain,
    schema: newSchema,
    embedding: embedding,
    existingPointId: existingPointId,
    hasExisting: existingPointId !== null
  }
}];

